import React from 'react';
import {api} from '../utils/Api.js';

function Main({onEditProfile, onAddPlace, onEditAvatar}) {
    const [userName, setUserName] = React.useState('');
    const [userDescription, setUserDescription] = React.useState('');
    const [userAvatar, setUserAvatar] = React.useState('');

    React.useEffect(() => {
        api
          .getProfile()
          .then((res) => {
            setUserName(res.name);
            setUserDescription(res.about);
            setUserAvatar(res.avatar);
          })
          .catch((err) => {
            console.log(err);
          });
      }, []);

    return (
        <main className="content">
            <section className="profile page__profile">
                <div className="profile__info">
                    <div className="profile__avatar-content">
                        <img src="<%=require('./images/profile_logo.jpg')%>" alt="Аватар профиля" className="profile__avatar" />
                        <div className="profile__avatar-edit" onClick={onEditAvatar}></div>
                    </div>
                    <div className="profile__text">
                        <div className="profile__name-edit">
                            <h1 className="profile__name">Жак-Ив Кусто</h1>
                            <button type = "button" className="profile__edit-button" onClick={onEditProfile}>
                            </button>
                        </div>
                        <h2 className="profile__description">Исследователь океана</h2> 
                    </div>
                </div>
                    <button type = "button" className="profile__add-button" onClick={onAddPlace}>
                    </button>
            </section>
            <section className="cards page__cards">
                <template className = "card-template">
                    <div className="card page__card">
                        <button type = "button" className="card__delete-button"></button>
                        <img src="#" alt="" className="card__image" />
                        <div className="card__container">
                            <h3 className="card__title"></h3>
                            <div className="card__like-container">
                                <button type = "button" className="card__like-button"></button>
                                <p className="card__like-counter">1</p>
                            </div>
                        </div>
                    </div>
                </template>
            </section>
        </main>
    );
}

export default Main;
