import React from 'react';
import Header from './Header';
import Main from './Main';
import Footer from './Footer' ;
import ImagePopup from './ImagePopup';
import CurrentUserContext from './CurrentUserContext';
import '../index.css';
import { api } from '../utils/Api';
import EditProfilePopup from './EditProfilePopup';
import EditAvatarPopup from './EditAvatarPopup';
import AddPlacePopup from './AddPlacePopup';

function App() {
    const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
    const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
    const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
    const [selectedCard, setSelectedCard] = React.useState({});
    const [isImagePopupOpen, setIsImagePopupOpen] = React.useState(false);
    const [currentUser, setCurrentUser] = React.useState({});
    const [cards, setCards] = React.useState([]);

    React.useEffect(() => {
        api.getUserInfo()
            .then(res => setCurrentUser(res))
            .catch(err => console.log(err));
    }, []);

    React.useEffect(() => {
        api.getInitialCards()
            .then(res => setCards(...cards, res))
            .catch(err => console.log(err))
    }, []);

    const handleEditProfileClick = () => {
        setIsEditProfilePopupOpen(true);
    };

    const handleAddPlaceClick = () => {
        setIsAddPlacePopupOpen(true);
    };

    const handleEditAvatarClick = () => {
        setIsEditAvatarPopupOpen(true);
    };

    const handleCardClick = (card) => {
        setSelectedCard(card);
        setIsImagePopupOpen(true);
    }

    const closeAllPopups = () => {
        setIsEditProfilePopupOpen(false);
        setIsAddPlacePopupOpen(false);
        setIsEditAvatarPopupOpen(false);
        setIsImagePopupOpen(false);
        setSelectedCard({});
    };

    const handleUpdateUser = (name, about) => {
        api.editProfileInfo(name, about)
            .then(res => {
                setCurrentUser(res);
                closeAllPopups();
            })
            .catch(err => {
                console.log(err)
            })
    }

    const handleUpdateAvatar = (avatar) => {
        api.editAvatar(avatar)
            .then(res => {
                setCurrentUser(res);
                closeAllPopups();
            })
            .catch(err => {
                console.log(err);
            })
    }

    const handleAddPlaceSubmit = (title, src) => {
        api.addNewCard()
            .then(card => {
                setCards([card, ...cards]);
                closeAllPopups();
            })
            .catch(err => console.log(err));
    }

    function handleCardLike(card) {
        const isLiked = card.likes.some(i => i._id === currentUser._id);

        isLiked ? api.dislikeCard(card._id, !isLiked)
            .then(newCard => {
                setCards(state => state.map(e => (
                e._id === card._id ? newCard : e
            )))
        })
            .catch(err => {
                console.log(err);
            })
            : api.likeCard(card._id, !isLiked)
                .then(newCard => {
                    setCards(state => state.map(e => (
                        e._id === card._id ? newCard : e
                    )))
                })
                .catch(err => {
                    console.log(err);
                })
      };

      function handleCardDelete(card) {
        api.deleteCard(card._id)
            .then((newArray) => {
          setCards((newArray) => newArray.filter((item) => card._id !== item._id));
        })
            .catch(err => {
                console.log(err);
            });
      }

  return (
    <CurrentUserContext.Provider value = {currentUser}>
        <div className="body">
        <div className="page">
            <Header />
            <Main 
                onEditProfile = {handleEditProfileClick}
                onAddPlace = {handleAddPlaceClick}
                onEditAvatar = {handleEditAvatarClick}
                onCardClick = {handleCardClick}
                cards = {cards}
                onCardLike = {handleCardLike}
                onCardDelete = {handleCardDelete}
            />
            <Footer />
        </div>
        <EditProfilePopup 
            isOpen = {isEditProfilePopupOpen}
            onClose = {closeAllPopups}
            onUpdateUser = {handleUpdateUser}
        />
        <AddPlacePopup 
            isOpen = {isAddPlacePopupOpen} 
            onClose = {closeAllPopups} 
            onAddPlace = {handleAddPlaceSubmit}/>
        <EditAvatarPopup
            isOpen={isEditAvatarPopupOpen} 
            onClose={closeAllPopups} 
            onUpdateAvatar = {handleUpdateAvatar}/>
        <div className="popup popup_type_delete-card">
            <div className="popup__overlay popup__overlay_type_delete-card"></div>
            <div className="popup__content">
                <button type = "button" className="popup__close popup__close_type_delete-card"></button>
                <h3 className="popup__title">Вы уверены?</h3>
                <form name = "popupForm" className="popup__form popup__form_type_delete-card" noValidate>
                    <button id = "delete-button" type = "submit" className="popup__save-button">Да</button>
                </form>
            </div>
        </div>
        <ImagePopup onClose = {closeAllPopups} card = {selectedCard} isOpen = {isImagePopupOpen} />
        </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
