import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Header.css';

function Header() {
    const location = useLocation().pathname;
    const isLanding = location === '/';

    const [isOpened, setOpened] = useState(false);

    const togglePopup = () => {
        setOpened((state) => !state);
        if (isOpened) {
            document.body.style.overflow = 'auto';
        } else {
            document.body.style.overflow = 'hidden';
        }
    };

    const closePopup = () => {
        setOpened(false);
        document.body.style.overflow = 'auto';
    };

    return (
        <header className="header">
            <Link className="header__logo" to="/" title="На главную" />
            { !isLanding ?  <Navigation visible={isOpened} onClose={closePopup} /> : (
                <nav className="header__auth">
                <ul className="header__auth-buttons">
                    <li>
                        <Link className="header__auth-button" to="/">
                            Регистрация
                        </Link>
                    </li> 
                    <li>
                        <Link className="header__auth-button header__auth-button_green-theme" to="/">
                            Войти
                        </Link>
                    </li>   
                </ul> 
            </nav>
              )}
            
        </header>
    )
}

export default Header;
