import React from 'react';
import {api} from '../utils/Api.js';
import Card from './Card.js';
import CurrentUserContext from './CurrentUserContext.js';

function Main({onEditProfile, onAddPlace, onEditAvatar, onCardClick}) {
    const {name, about, avatar, _id} = React.useContext(CurrentUserContext);
    const [cards, setCards] = React.useState([]);

      React.useEffect(() => {
        api.getInitialCards()
            .then((res) => setCards(...cards, res))
            .catch((err) => {
                console.log(err);
            });
      }, []);

      function handleCardLike(card) {
        const isLiked = card.likes.some(i => i._id === _id);

        isLiked ? api.dislikeCard(card._id, !isLiked)
            .then(newCard => {
                setCards(state => state.map(e => (
                e._id === card._id ? newCard : e
            )))
        })
            .catch(err => {
                console.log(err);
            })
            : api.likeCard(card._id, !isLiked)
                .then(newCard => {
                    setCards(state => state.map(e => (
                        e._id === card._id ? newCard : e
                    )))
                })
                .catch(err => {
                    console.log(err);
                })
      }

      function handleCardDelete(card) {
        api.deleteCard(card._id)
            .then((newArray) => {
          setCards((newArray) => newArray.filter((item) => card._id !== item._id));
        })
            .catch(err => {
                console.log(err);
            });
      }

    return (
        <main className="content">
            <section className="profile page__profile">
                <div className="profile__info">
                    <div className="profile__avatar-content">
                        <img src={avatar} alt="Аватар профиля" className="profile__avatar" />
                        <div className="profile__avatar-edit" onClick={onEditAvatar}></div>
                    </div>
                    <div className="profile__text">
                        <div className="profile__name-edit">
                            <h1 className="profile__name">{name}</h1>
                            <button type = "button" className="profile__edit-button" onClick={onEditProfile}>
                            </button>
                        </div>
                        <h2 className="profile__description">{about}</h2> 
                    </div>
                </div>
                    <button type = "button" className="profile__add-button" onClick={onAddPlace}>
                    </button>
            </section>
            <section className="cards page__cards">
                    {cards.map(card => { 
                        return (
                            <Card title = {card.name} 
                                  key = {card._id} 
                                  link = {card.link} 
                                  likes = {card.likes.length} 
                                  onCardClick = {onCardClick} 
                                  card = {card} 
                                  onCardLike = {handleCardLike} 
                                  onCardDelete = {handleCardDelete}
                            />
                        )
                    })}
            </section>
        </main>
    );
}

export default Main;
