import React from 'react';

import Header from './Header';
import Main from './Main';
import Footer from './Footer' ;
import PopupWithForm from './PopupWithForm';

import '../index.css';

function App() {
    const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
    const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
    const [isEditAvatarPopupOpenm, setIsEditAvatarPopupOpen] = React.useState(false);

    const handleEditProfileClick = () => {
        setIsEditProfilePopupOpen(true);
    };

    const handleAddPlaceClick = () => {
        setIsAddPlacePopupOpen(true);
    };

    const handleEditAvatarClick = () => {
        setIsEditAvatarPopupOpen(true);
    };

    const handleClosePopupClick = () => {
        setIsEditProfilePopupOpen(false);
        setIsAddPlacePopupOpen(false);
        setIsEditAvatarPopupOpen(false);
    };

  return (
    <div className="body">
      <div className="page">
        <Header />
        <Main 
            onEditProfile = {handleEditProfileClick}
            onAddPlace = {handleAddPlaceClick}
            onEditAvatar = {handleEditAvatarClick}
        />
        <Footer />
    </div>
    <PopupWithForm 
        title = "Редактировать профиль"
        name = "edit-name"
        isPopupOpen={isEditProfilePopupOpen}
        onClose = {handleClosePopupClick}
        children = {
            <>
                <input id = "name-input" type="text" name = "firstname" placeholder = "Имя" className="popup__input popup__input_content_name" required minLength="2" maxLength="40" />
                <span className = "name-input-error popup__input-error"></span>
                <input id = "description-input" type="text" name = "description" placeholder = "Описание профиля" className="popup__input popup__input_content_description" required minLength="2" maxLength="200" />
                <span className = "description-input-error popup__input-error"></span>
            </>
        }
        />
    <PopupWithForm 
        title = "Новое место"
        name = "add-card"
        isPopupOpen={isAddPlacePopupOpen}
        onClose = {handleClosePopupClick}
        children = {
            <>
                <input id = "place-name-input" type="text" name = "name" placeholder = "Название" className="popup__input popup__input_content_title" required minLength="2" maxLength="30" />
                <span className = "place-name-input-error popup__input-error"></span>
                <input id = "place-src-input" type="url" name = "link" placeholder = "Ссылка на картинку" className="popup__input popup__input_content_src" required />
                <span className = "place-src-input-error popup__input-error"></span>
            </>
        }
        />
    <PopupWithForm 
        title = "Обновить аватар"
        name = "edit-avatar"
        isPopupOpen={isEditAvatarPopupOpen}
        onClose = {handleClosePopupClick}
        children = {
            <>
                <input id = "place-name-input" type="text" name = "name" placeholder = "Название" className="popup__input popup__input_content_title" required minLength="2" maxLength="30" />
                <span className = "place-name-input-error popup__input-error"></span>
                <input id = "place-src-input" type="url" name = "link" placeholder = "Ссылка на картинку" className="popup__input popup__input_content_src" required />
                <span className = "place-src-input-error popup__input-error"></span>
            </>
        }
        />    
    <div className="popup popup_type_fullscreen-card">
        <div className="popup__overlay popup__overlay_type_fullscreen-card"></div>
        <div className="popup__content popup__content_type_fullscreen-card">
            <img src="#" alt="" className="popup__image" />
            <button type = "button" className="popup__close popup__close_type_fullscreen-card"></button>
            <h3 className="popup__title popup__title_type_fullscreen-card"></h3>
        </div>
    </div>
    <div className="popup popup_type_delete-card">
        <div className="popup__overlay popup__overlay_type_delete-card"></div>
        <div className="popup__content">
            <button type = "button" className="popup__close popup__close_type_delete-card"></button>
            <h3 className="popup__title">Вы уверены?</h3>
            <form name = "popupForm" className="popup__form popup__form_type_delete-card" noValidate>
                <button id = "delete-button" type = "submit" className="popup__save-button">Да</button>
            </form>
        </div>
    </div>
    <div className="popup popup_type_edit-avatar">
        <div className="popup__overlay popup__overlay_type_edit-avatar"></div>
        <div className="popup__content">
            <button type = "button" className="popup__close popup__close_type_edit-avatar"></button>
            <h3 className="popup__title">Обновить аватар</h3>
            <form name = "popupForm" className="popup__form popup__form_type_edit-avatar" noValidate>
                <input id = "avatar-src-input" type="url" name = "link" placeholder = "Ссылка на картинку" className="popup__input popup__input_content_src" required />
                <span className = "avatar-src-input-error popup__input-error"></span>
                <button id = "save-changes-button" type = "submit" className="popup__save-button">Сохранить</button>
            </form>
        </div>
    </div>
    </div>
  );
}

export default App;
