import React from 'react';
import CurrentUserContext from './CurrentUserContext.js';
import PopupWithForm from './PopupWithForm.js';

function EditProfilePopup({isOpen, onClose}) {
    const [name, setName] = React.useState('');
    const [description, setDescription] = React.useState('');
    const currentUser = React.useContext(CurrentUserContext);

    React.useEffect(() => {
        setName(currentUser.name);
        setDescription(currentUser.description);
    }, [currentUser])

    return (
        <PopupWithForm
            title = "Редактировать профиль"
            name = "edit-name"
            isPopupOpen={isEditProfilePopupOpen}
            onClose = {closeAllPopups}
            children = {
                <>
                    <input id = "name-input" type="text" name = "firstname" placeholder = "Имя" className="popup__input popup__input_content_name" required minLength="2" maxLength="40" />
                    <span className = "name-input-error popup__input-error"></span>
                    <input id = "description-input" type="text" name = "description" placeholder = "Описание профиля" className="popup__input popup__input_content_description" required minLength="2" maxLength="200" />
                    <span className = "description-input-error popup__input-error"></span>
                </>
            }
            buttonText = "Сохранить"
            />
    );
}

export default EditProfilePopup;
