import React from 'react';
import { Link, NavLink } from 'react-router-dom';
import './MoviesHeader.css';

function MoviesHeader() {
    return (
        <div className="movies-header">
            <Link className="movies-header__logo" to="/" title="На главную" />
            <ul className="movies-header__nav-items">
                <li className="movies-header__nav-item">
                    <NavLink to="/movies" className={({isActive}) => isActive ? "" : ""}>Фильмы</NavLink>
                </li>
                <li className="movies-header__nav-item">
                    <NavLink to="/saved-movies" className={({isActive}) => isActive ? "" : ""}>Сохранённые фильмы</NavLink>
                </li>
            </ul>
            <Link className="movies-header__profile-button" to="/profile" title="На главную">Аккаунт</Link>
        </div>
    )
}

export default MoviesHeader;
